import React, {useState} from "react";
import img from "../assets/login-img.png";

function RegistrationForm({ Login, error }) {

    const [register, setRegister] = useState({name: "",age: "", email: "", password: ""});
    const [name, setName] = useState(null);
    const [age, setAge] = useState(null);
    const [email, setEmail] = useState(null);
    const [password,setPassword] = useState(null);

    const submitHandler = e => {
        e.preventDefault();
        Login(register);
    }

    const handleInputChange = (e) => {
        const {id , value} = e.target;
        if(id === "name"){
            setName(value);
        }
        if(id === "age"){
            setAge(value);
        }
        if(id === "email"){
            setEmail(value);
        }
        if(id === "password"){
            setPassword(value);
        }
    }

    const handleSubmit  = () => {
        console.log(name,age,email,password);
    }

    return (
        <form onSubmit={submitHandler}>
            <div className="form-inner, container-left">
                <h2 className="text-left" >Register</h2>
                {(error !== "") ? (<div className="error">{error}</div> ) : ""}
                <div className="form-group">
                    <label htmlFor="name">Name: </label>
                    <input className="form__input" type="text" value={name} onChange = {e => handleInputChange(e)} id="name" placeholder="Name"/>

                </div>
                <div className="form-group">
                    <label htmlFor="age">Age: </label>
                    <input className="form__input" type="number" value={age} onChange = {e => handleInputChange(e)} id="age" />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email: </label>
                    <input  type="email" id="email" className="form__input" value={email} onChange = {e => handleInputChange(e)} placeholder="Email"/>
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password: </label>
                    <input className="form__input" type="password"  id="password" value={password} onChange = {e => handleInputChange(e)} placeholder="Password"/>
                </div>
                <button onClick={() => handleSubmit()} type="submit" className="btn">Register</button>
            </div>
            <div className="container-right">
                <img src={img} alt={"img"}/>
            </div>
        </form>
    )
}

export default RegistrationForm
